INSERT INTO `denverPages` (`pgName`, `pgContent`) VALUES
('comments', '<?php\r\n	if(!empty($_POST)) {\r\n		// POST processing\r\n		$safeName = \'/^[A-Za-z ]+$/\';\r\n		$safeComment = \'/^[A-Za-z\\d \\\'\",.!?()#]+$/\';\r\n\r\n		function test_input($data){\r\n			$data = trim($data);\r\n			$data = stripslashes($data);\r\n			$data = htmlspecialchars($data);\r\n			$data = filter_var($data, FILTER_SANITIZE_STRING, FILTER_FLAG_STRIP_HIGH);\r\n			return $data;\r\n		}\r\n		\r\n		$name = test_input($_POST[\'name\']);\r\n		$comment = test_input($_POST[\'comment\']);\r\n\r\n		if ($mysqli) {\r\n			if (preg_match($safeName, $name) && !empty($name)){\r\n				if (preg_match($safeComment, $comment) && !empty($comment)){\r\n					$stmt=$mysqli->prepare(\'INSERT INTO denverComments (id, name, comment) VALUES (0, ?, ?)\');\r\n					$stmt->bind_param(\'ss\', $name, $comment);\r\n					$stmt->execute();	\r\n					$stmt->close();\r\n          $_POST = array();\r\n        }\r\n      }\r\n		}\r\n	}\r\n?>\r\n<!--HTML START-->\r\n<div class=\"container main\"> <!-- Main container -->\r\n	<div class=\"row align-items-start justify-content-center\"> <!-- Main row -->\r\n		<fieldset class=\"col-md-7 card mt-4 pb-4\" id=\"contentBody\"> <!-- Main content column -->\r\n		<legend class=\"legLabel card ml-2 pl-2\">Leave a Comment</legend>\r\n			<form class=\"form-horizontal card p-3 mb-1\" name=\"Comment Form\" id=\'commentForm\' onsubmit=\'return validateComment();\' action=\"<?PHP echo $goComm; ?>\" method=\"POST\">\r\n				<div class=\"form-row\"> <!-- form row -->\r\n					<div class=\"form-group col-md-12\"> <!-- name input -->\r\n						<label for=\"name\" class=\"form-control-label\">Name</label>\r\n						<input type=\"text\" name=\"name\" id=\"name\" placeholder=\"Jane Doe\"\r\n							<?php \r\n								if(isset($name)) {\r\n									echo \'value=\"\'.$name.\'\" class=\"form-control\"\';\r\n								}\r\n								elseif (empty($name) && isset($_POST[\'name\'])){\r\n									echo \'value=\"\" class=\"form-control is-invalid\"\';\r\n								}\r\n								else{\r\n									echo \'value=\"\" class=\"form-control\"\';\r\n								}\r\n							?>\r\n						>\r\n						<div id=\"nameValid\" class=\"invalid-feedback <?php if (empty($name) && isset($_POST[\'name\'])){ echo \'d-block\';}?>\">Please provide a proper name.</div>\r\n					</div> <!-- end name input -->\r\n				</div> <!-- end form row -->\r\n				<!-- Comment -->\r\n				<div class=\"form-row\">\r\n					<div class=\"form-group col-md-12\"> <!-- comment input -->\r\n						<label for=\"comment\" class=\"form-control-label\">Comment</label>\r\n						<textarea name=\"comment\" rows=\"5\" cols=\"140\" id=\"comment\" placeholder=\"Denver is awsome!\" \r\n								<?php \r\n									if(isset($comment)) {\r\n										echo \'value=\"\'.$comment.\'\" class=\"form-control\"\';\r\n									}\r\n									elseif (empty($comment) && isset($_POST[\'comment\'])){\r\n										echo \'value=\"\" class=\"form-control is-invalid\"\';\r\n									}\r\n									else{\r\n										echo \'value=\"\" class=\"form-control\"\';\r\n									}\r\n								?>\r\n						></textarea>\r\n						<div id=\"commentValid\" class=\"invalid-feedback <?php if (empty($comment) && isset($_POST[\'comment\'])){ echo \'d-block\';}?>\">Please enter a comment.</div>\r\n					</div><!-- end comment input -->\r\n				</div> <!-- end form row -->\r\n				<!-- Submit -->\r\n				<div class=\"form-group row\">\r\n					<div class=\"col-sm-offset-2 col-xl-12\">\r\n					<button type=\"submit\" class=\"btn btn-primary\">Submit</button>\r\n					</div>\r\n				</div>\r\n			</form> <!-- end form -->\r\n		</fieldset> <!-- end main content column -->\r\n		\r\n		<fieldset class=\"col-md-6 card mt-4 px-4 pt-4 pb-1 mb-5 fieldsetDiv\" id=\"contentBody\"> <!-- comment column -->\r\n		<legend class=\"legLabel card ml-2 pl-2\">Comments:</legend>\r\n			<?php\r\n				//get contents of table and send back...\r\n				$records = array();\r\n				$sqlComm = \'select name, comment, date from denverComments\';\r\n				$resComm=$mysqli->query($sqlComm);\r\n				if($resComm){\r\n					while($rowHolder = mysqli_fetch_array($resComm,MYSQLI_ASSOC)){\r\n						$records[] = $rowHolder;\r\n					}\r\n				}\r\n				//var_dump($records);\r\n			?>\r\n			<?php\r\n				//print records\r\n				foreach($records as $value):\r\n			?>\r\n				<div class=\'col-sm-12 card mb-3 px-0\'>\r\n					<div class=\'card-header\'>\r\n						<strong> <?php echo $value[\'name\']; ?></strong>\r\n						<span class=\'text-muted\'>commented on <?php echo $value[\'date\']; ?></span>\r\n					</div>\r\n					<div class=\'card-body ml-2\'>\r\n						<?php echo $value[\'comment\']; ?>\r\n					</div>\r\n				</div>\r\n			<?php endforeach;?>\r\n		</fieldset>\r\n	</div> <!-- end main content row -->\r\n</div> <!-- end main container -->\r\n<script src=\"./assets/js/comments.js\"></script>\r\n<!--HTML END-->'),
('contact', '<!-- HTML STARTS -->\r\n<div class=\"container main\">\r\n  <div class=\"card m-5 p-5 has-bg\" id=\"info\">\r\n    <div class=\"row\">\r\n      <div class=\"col-8 mx-auto frost\">\r\n        <h2 class=\"display-4\">Travel / Tourism Information</h2>\r\n        <br>\r\n        <p class=\"text-center\">Call: (800) 233-6837 (Toll Free)\r\n          Call: (303) 892-1505 (Charges Apply)<br>\r\n          Email: visitorinfo@visitdenver.com<br>\r\n          Facebook: visitdenver<br>\r\n          Twitter: @visitdenver<br>\r\n          In-person: Tourist Information Center, 1575 California St.<br>\r\n        </p>\r\n        <br>\r\n        <h3 class=\"display-5\">ADDITIONAL QUESTIONS / INQUIRIES</h3>\r\n        <br>\r\n        <p class=\"text-center\">Media: press@visitdenver.com<br>\r\n          Partnership: partnership@visitdenver.com<br>\r\n          Employment Opportunities: Visit our careers section<br>\r\n          Convention Sales/Services: (800) 480-2010 (Toll Free)<br>\r\n        </p>\r\n      </div>\r\n    </div>\r\n  </div>\r\n  <div class=\"card m-5 p-5 has-bg\" id=\"map\">\r\n    <div class=\"row\">\r\n      <div class=\"col-8 mx-auto frost\">\r\n        <h2 class=\"display-4\"><a href=\"https://www.denver.org/listing/downtown-denver-tourist-information-center/4810/\">Tourist Information Center</a></h2>\r\n        <p class=\"text-center text-muted\">1575 California St., Denver, CO 80202</p>\r\n      </div>\r\n      <div class=\"col-8 mx-auto align-items-start embed-responsive embed-responsive-4by3\">\r\n        <iframe class=\"embed-responsive-item\" src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d98187.8872167582!2d-105.03684911869134!3d39.731305726165424!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x876c78d0d57547f5%3A0x3cf6a3766afc9777!2sDenver+Tourist+Information+Center!5e0!3m2!1sen!2sus!4v1539872414170\" width=\"600\" height=\"450\"   allowfullscreen></iframe>\r\n      </div>\r\n    </div>\r\n  </div>\r\n</div>\r\n<!-- HTML ENDS -->'),
